select  task_id, json_extract(json_data, '$.outputData.waitForFileStatus') mamStat, json_extract(json_data, '$.taskStatus') taskStatus, modified_on from task where json_extract(json_data, '$.taskType')='transfer_and_wait_task_v1'  and json_extract(json_data, '$.taskStatus')='IN_PROGRESS'  and json_extract(json_data, '$.outputData.waitForFileStatus') = 'FOUND'  order by modified_on limit 50;
